mapper.select:
SELECT
FROM APPLICANT_INFO_TBL LEFT JOIN USER_TBL ON APPLICANT_INFO_TBL.USER_CD = USER_TBL.USER_CD LEFT JOIN COU_MST ON APPLICANT_INFO_TBL.COUNTRY_ID = COU_MST.COUNTRY_ID LEFT JOIN ( SELECT A.APPLICANT_ID , B.INDUSTRY_ID INDUSTRY_ID1 , C.INDUSTRY_ID INDUSTRY_ID2 , D.INDUSTRY_ID INDUSTRY_ID3 , B.INDUSTRY_NM INDUSTRY_NM1 , C.INDUSTRY_NM INDUSTRY_NM2 , D.INDUSTRY_NM INDUSTRY_NM3 FROM APPLICANT_INFO_TBL A LEFT JOIN INDUSTRY_MST B ON A.INDUSTRY_ID1 = B.INDUSTRY_ID LEFT JOIN INDUSTRY_MST C ON A.INDUSTRY_ID2 = C.INDUSTRY_ID LEFT JOIN INDUSTRY_MST D ON A.INDUSTRY_ID3 = D.INDUSTRY_ID WHERE B.DEL_FLG = '0' AND C.DEL_FLG = '0' AND D.DEL_FLG = '0' ) INDUSTRY_SELECT ON INDUSTRY_SELECT.APPLICANT_ID = APPLICANT_INFO_TBL.APPLICANT_ID LEFT JOIN ( SELECT A.APPLICANT_ID , B.JOBTYPE_ID JOBTYPE_ID1_1 , C.JOBTYPE_ID JOBTYPE_ID1_2 , D.JOBTYPE_ID JOBTYPE_ID1_3 , E.JOBTYPE_ID JOBTYPE_ID2_1 , F.JOBTYPE_ID JOBTYPE_ID2_2 , G.JOBTYPE_ID JOBTYPE_ID2_3 , H.JOBTYPE_ID JOBTYPE_ID3_1 , I.JOBTYPE_ID JOBTYPE_ID3_2 , J.JOBTYPE_ID JOBTYPE_ID3_3 , B.JOBTYPE_NM JOBTYPE_NM1_1 , C.JOBTYPE_NM JOBTYPE_NM1_2 , D.JOBTYPE_NM JOBTYPE_NM1_3 , E.JOBTYPE_NM JOBTYPE_NM2_1 , F.JOBTYPE_NM JOBTYPE_NM2_2 , G.JOBTYPE_NM JOBTYPE_NM2_3 , H.JOBTYPE_NM JOBTYPE_NM3_1 , I.JOBTYPE_NM JOBTYPE_NM3_2 , J.JOBTYPE_NM JOBTYPE_NM3_3 FROM APPLICANT_INFO_TBL A LEFT JOIN JOBTYPE_MST B ON A.JOBTYPE_ID1_1 = B.JOBTYPE_ID LEFT JOIN JOBTYPE_MST C ON A.JOBTYPE_ID1_2 = C.JOBTYPE_ID LEFT JOIN JOBTYPE_MST D ON A.JOBTYPE_ID1_3 = D.JOBTYPE_ID LEFT JOIN JOBTYPE_MST E ON A.JOBTYPE_ID2_1 = E.JOBTYPE_ID LEFT JOIN JOBTYPE_MST F ON A.JOBTYPE_ID2_2 = F.JOBTYPE_ID LEFT JOIN JOBTYPE_MST G ON A.JOBTYPE_ID2_3 = G.JOBTYPE_ID LEFT JOIN JOBTYPE_MST H ON A.JOBTYPE_ID3_1 = H.JOBTYPE_ID LEFT JOIN JOBTYPE_MST I ON A.JOBTYPE_ID3_2 = I.JOBTYPE_ID LEFT JOIN JOBTYPE_MST J ON A.JOBTYPE_ID3_3 = J.JOBTYPE_ID WHERE B.DEL_FLG = '0' AND C.DEL_FLG = '0' AND D.DEL_FLG = '0' AND E.DEL_FLG = '0' AND F.DEL_FLG = '0' AND G.DEL_FLG = '0' AND H.DEL_FLG = '0' AND I.DEL_FLG = '0' AND J.DEL_FLG = '0' ) JOBTYPE_SELECT ON JOBTYPE_SELECT.APPLICANT_ID = APPLICANT_INFO_TBL.APPLICANT_ID LEFT JOIN ( SELECT A.APPLICANT_ID , B.PREFE_ID as HOPE_LANDID1 , C.PREFE_ID as HOPE_LANDID2 , D.PREFE_ID as HOPE_LANDID3 , B.PREFE_NM as HOPE_LANDNM1 , C.PREFE_NM as HOPE_LANDNM2 , D.PREFE_NM as HOPE_LANDNM3 FROM APPLICANT_INFO_TBL A LEFT JOIN PREFECTURES_MST B ON A.HOPE_LAND1 = B.PREFE_ID LEFT JOIN PREFECTURES_MST C ON A.HOPE_LAND2 = C.PREFE_ID LEFT JOIN PREFECTURES_MST D ON A.HOPE_LAND3 = D.PREFE_ID WHERE B.DEL_FLG = '0' AND C.DEL_FLG = '0' AND D.DEL_FLG = '0' ) PREFECTURES_SELECT ON PREFECTURES_SELECT.APPLICANT_ID = APPLICANT_INFO_TBL.APPLICANT_ID
INNER JOIN APPLICANT_LAN_TBL ON APPLICANT_INFO_TBL.APPLICANT_ID = APPLICANT_LAN_TBL.APPLICANT_ID AND APPLICANT_LAN_TBL.DEL_FLG = '0'
WHERE USER_TBL.DEL_FLG = '0' AND APPLICANT_INFO_TBL.DEL_FLG = '0'
AND APPLICANT_LAN_TBL.LANGUAGE_ID = #{a0201VueForm.languageId}
AND APPLICANT_LAN_TBL.LEVEL_SECTION >= #{a0201VueForm.levelSection}
AND #{a0201VueForm.industryId} IN(INDUSTRY_SELECT.INDUSTRY_ID1,INDUSTRY_SELECT.INDUSTRY_ID2,INDUSTRY_SELECT.INDUSTRY_ID3)
AND #{a0201VueForm.jobtypeId} IN(JOBTYPE_SELECT.JOBTYPE_ID1_1,JOBTYPE_SELECT.JOBTYPE_ID1_2,JOBTYPE_SELECT.JOBTYPE_ID1_3 ,JOBTYPE_SELECT.JOBTYPE_ID2_1,JOBTYPE_SELECT.JOBTYPE_ID2_2,JOBTYPE_SELECT.JOBTYPE_ID2_3 ,JOBTYPE_SELECT.JOBTYPE_ID3_1,JOBTYPE_SELECT.JOBTYPE_ID3_2,JOBTYPE_SELECT.JOBTYPE_ID3_3 )
AND #{a0201VueForm.hopeLand} IN(PREFECTURES_SELECT.HOPE_LANDID1,PREFECTURES_SELECT.HOPE_LANDID2,PREFECTURES_SELECT.HOPE_LANDID3)
AND USER_TBL.FIRST_NAME || USER_TBL.LAST_NAME LIKE '%${a0201VueForm.ambiguousBox}%'
AND APPLICANT_INFO_TBL.FINAL_EDUCATION = #{a0201VueForm.degree}
AND APPLICANT_INFO_TBL.BIRTHDAY >= #{a0201VueForm.birthdayFrom}
AND APPLICANT_INFO_TBL.BIRTHDAY <= #{a0201VueForm.birthdayTo}
AND APPLICANT_INFO_TBL.HOPE_SALARY_FROM >= #{a0201VueForm.hopeSalaryFrom}
AND APPLICANT_INFO_TBL.HOPE_SALARY_TO <= #{a0201VueForm.hopeSalaryTo}
AND APPLICANT_INFO_TBL.WORK_YEARS <= #{a0201VueForm.workYears}
AND APPLICANT_INFO_TBL.PREFE_NM = #{a0201VueForm.prefeNm}
AND USER_TBL.SEX = #{a0201VueForm.sex}
GROUP BY APPLICANT_INFO_TBL.APPLICANT_ID ,APPLICANT_INFO_TBL.USER_CD ,USER_TBL.FIRST_NAME ,USER_TBL.LAST_NAME ,INDUSTRY_SELECT.INDUSTRY_ID1 ,INDUSTRY_SELECT.INDUSTRY_ID2 ,INDUSTRY_SELECT.INDUSTRY_ID3 ,INDUSTRY_SELECT.INDUSTRY_NM1 ,INDUSTRY_SELECT.INDUSTRY_NM2 ,INDUSTRY_SELECT.INDUSTRY_NM3 ,JOBTYPE_SELECT.JOBTYPE_ID1_1 ,JOBTYPE_SELECT.JOBTYPE_ID1_2 ,JOBTYPE_SELECT.JOBTYPE_ID1_3 ,JOBTYPE_SELECT.JOBTYPE_ID2_1 ,JOBTYPE_SELECT.JOBTYPE_ID2_2 ,JOBTYPE_SELECT.JOBTYPE_ID2_3 ,JOBTYPE_SELECT.JOBTYPE_ID3_1 ,JOBTYPE_SELECT.JOBTYPE_ID3_2 ,JOBTYPE_SELECT.JOBTYPE_ID3_3 ,JOBTYPE_SELECT.JOBTYPE_NM1_1 ,JOBTYPE_SELECT.JOBTYPE_NM1_2 ,JOBTYPE_SELECT.JOBTYPE_NM1_3 ,JOBTYPE_SELECT.JOBTYPE_NM2_1 ,JOBTYPE_SELECT.JOBTYPE_NM2_2 ,JOBTYPE_SELECT.JOBTYPE_NM2_3 ,JOBTYPE_SELECT.JOBTYPE_NM3_1 ,JOBTYPE_SELECT.JOBTYPE_NM3_2 ,JOBTYPE_SELECT.JOBTYPE_NM3_3 ,PREFECTURES_SELECT.HOPE_LANDID1 ,PREFECTURES_SELECT.HOPE_LANDID2 ,PREFECTURES_SELECT.HOPE_LANDID3 ,PREFECTURES_SELECT.HOPE_LANDNM1 ,PREFECTURES_SELECT.HOPE_LANDNM2 ,PREFECTURES_SELECT.HOPE_LANDNM3 ,APPLICANT_INFO_TBL.PREFE_NM ,APPLICANT_INFO_TBL.STATION ,APPLICANT_INFO_TBL.ADDRESS1 ,APPLICANT_INFO_TBL.FINAL_EDUCATION ,APPLICANT_INFO_TBL.GRADUATION_SCHOOL ,APPLICANT_INFO_TBL.BIRTHDAY ,APPLICANT_INFO_TBL.WORK_SITUATION ,USER_TBL.SEX ,COU_MST.COUNTRY_ID ,COU_MST.COUNTRY_NM ,APPLICANT_INFO_TBL.HOPE_SALARY_FROM ,APPLICANT_INFO_TBL.HOPE_SALARY_TO ,USER_TBL.USER_MAIL ,APPLICANT_INFO_TBL.PHOTO ORDER BY APPLICANT_INFO_TBL.UPDATE_TIME DESC

mapper.update:
UPDATE APPLICANT_INFO_TBL
BIRTHDAY = #{c010101VueForm.birthday}, APPLICANT_POSTAL = #{c010101VueForm.applicantPostal}, COUNTRY_ID = #{c010101VueForm.countryId}, PREFE_NM = #{c010101VueForm.prefeNm}, ADDRESS1 = #{c010101VueForm.address1}, ADDRESS2 = #{c010101VueForm.address2}, STATION = #{c010101VueForm.station}, HOPE_LAND1 = #{c010101VueForm.hopeLand1}, HOPE_LAND2 = #{c010101VueForm.hopeLand2}, HOPE_LAND3 = #{c010101VueForm.hopeLand3}, PHOTO = #{c010101VueForm.photo}, WORK_SITUATION = #{c010101VueForm.workSituation}, GRADUATION_SCHOOL = #{c010101VueForm.graduationSchool}, GRADUATION_MAJOR = #{c010101VueForm.graduationMajor}, WORK_YEARS = #{c010101VueForm.workYears}, INDUSTRY_ID1 = #{c010101VueForm.industryId1}, JOBTYPE_ID1_1 = #{c010101VueForm.jobtypeId1_1}, JOBTYPE_ID1_2 = #{c010101VueForm.jobtypeId1_2}, JOBTYPE_ID1_3 = #{c010101VueForm.jobtypeId1_3}, INDUSTRY_ID2 = #{c010101VueForm.industryId2}, JOBTYPE_ID2_1 = #{c010101VueForm.jobtypeId2_1}, JOBTYPE_ID2_2 = #{c010101VueForm.jobtypeId2_2}, JOBTYPE_ID2_3 = #{c010101VueForm.jobtypeId2_3}, INDUSTRY_ID3 = #{c010101VueForm.industryId3}, JOBTYPE_ID3_1 = #{c010101VueForm.jobtypeId3_1}, JOBTYPE_ID3_2 = #{c010101VueForm.jobtypeId3_2}, JOBTYPE_ID3_3 = #{c010101VueForm.jobtypeId3_3}, FINAL_EDUCATION = #{c010101VueForm.finalEducation}, HOPE_SALARY_FROM = #{c010101VueForm.hopeSalaryFrom}, HOPE_SALARY_TO = #{c010101VueForm.hopeSalaryTo}, INTRODUCE = #{c010101VueForm.introduce}, REASON = #{c010101VueForm.reason}, SPECIALTY = #{c010101VueForm.specialty}, INTEREST = #{c010101VueForm.interest}, REMARKS = #{c010101VueForm.remarks}, UPDATE_TIME = CURRENT_TIMESTAMP(0), UPDATE_BY = #{c010101VueForm.userCd}
WHERE APPLICANT_ID = #{c010101VueForm.applicantId}
AND USER_CD = #{c010101VueForm.userCd}
AND DEL_FLG = '0'

